import React, { useState } from "react";
import { Stage, Layer, Line, Circle, Group } from "react-konva";

export function ChinMarker({ position, onDragMove }) {
  const lineLength = 50;
  const gapLength = 10;
  const lineColor = "black";
  const circleColor = "red";
  const lineWidth = 2;
  const canvasSize = 1000; // set the canvas size

  const [rotation, setRotation] = useState(0);

  const leftLinePoints = [0, 0, lineLength / 2 - gapLength / 2, 0];
  const rightLinePoints = [lineLength / 2 + gapLength / 2, 0, lineLength, 0];

  const circlePosition = { x: lineLength / 2, y: 0 };

  const rotateForward = () => {
    setRotation(rotation + 5);
  };

  const rotateBackward = () => {
    setRotation(rotation - 5);
  };

  return (
    <div>
      <button onClick={rotateForward}>Rotate Forward</button>
      <button onClick={rotateBackward}>Rotate Backward</button>
      <Stage
        width={canvasSize}
        height={canvasSize}
        draggable
        onDragMove={onDragMove}
        position={position}
      >
        <Layer>
          <Group rotation={rotation} offsetX={circlePosition.x}>
            <Line
              points={leftLinePoints}
              stroke={lineColor}
              strokeWidth={lineWidth}
            />
            <Line
              points={rightLinePoints}
              stroke={lineColor}
              strokeWidth={lineWidth}
            />
            <Circle
              radius={lineWidth * 2}
              fill={circleColor}
              {...circlePosition}
            />
          </Group>
        </Layer>
      </Stage>
    </div>
  );
}
