import React, { useState } from "react";

function MeasureImprovement() {
  const [beforeImage, setBeforeImage] = useState(null);
  const [afterImage, setAfterImage] = useState(null);

  const handleBeforeImageUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onloadend = () => {
      setBeforeImage(reader.result);
    };

    reader.readAsDataURL(file);
  };

  const handleAfterImageUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onloadend = () => {
      setAfterImage(reader.result);
    };

    reader.readAsDataURL(file);
  };

  return (
    <div>
      <h1>Measure Improvement Page</h1>
      <input type="file" accept="image/*" onChange={handleBeforeImageUpload} />
      {beforeImage && (
        <img
          src={beforeImage}
          alt="Uploaded Before"
          style={{ maxWidth: "200px" }}
        />
      )}
      <input type="file" accept="image/*" onChange={handleAfterImageUpload} />
      {afterImage && (
        <img
          src={afterImage}
          alt="Uploaded After"
          style={{ maxWidth: "200px" }}
        />
      )}
    </div>
  );
}

export default MeasureImprovement;
