import React, { useState } from "react";
import ImageMarker from "react-image-marker";

function MeasureForwardGrowth() {
  const [image, setImage] = useState(null);
  const [markers, setMarkers] = useState([]);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onloadend = () => {
      setImage(reader.result);
    };

    reader.readAsDataURL(file);
  };

  const handleMarkerCreation = (marker) => {
    setMarkers([...markers, marker]);
  };

  return (
    <div>
      <h1>Measure Forward Growth Page</h1>
      <input type="file" accept="image/*" onChange={handleImageUpload} />
      {image && (
        <>
          <h3>Please Label the 5 cm Marker on this Image</h3>
          <ImageMarker
            src={image}
            markers={markers}
            onMarkerClick={(marker) => console.log(marker)}
            onMarkerCreated={handleMarkerCreation}
          />
        </>
      )}
    </div>
  );
}

export default MeasureForwardGrowth;
