import React, { useState } from "react";
import ImageMarker, { Marker } from "react-image-marker";
import growthGuide from "./growth-guide.jpeg";

function MeasureForwardGrowth() {
  const [image, setImage] = useState(null);
  const [markers, setMarkers] = useState([]);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onloadend = () => {
      setImage(reader.result);
    };

    reader.readAsDataURL(file);
  };

  const labelText = () => {
    if (markers.length <= 2) {
      return "Please Label the 5 cm Marker on this Image";
    } else if (markers.length <= 3) {
      return "Please Label point F - refer to above growth guide";
    } else if (markers.length <= 4) {
      return "Please Label point N - refer to above growth guide";
    } else {
      return "Please Label Gnathion - refer to above growth guide";
    }
  };

  return (
    <div>
      <h1>Measure Forward Growth Page</h1>
      <img
        src={growthGuide}
        alt="Gnathiometer Growth Guide"
        width="200px"
        height="200px"
      />

      <input type="file" accept="image/*" onChange={handleImageUpload} />
      {image && (
        <>
          <h3>Please Label the 5 cm Marker on this Image</h3>
          <ImageMarker
            src={image}
            markers={markers}
            onAddMarker={(marker) => setMarkers([...markers, marker])}
          />
        </>
      )}
    </div>
  );
}

export default MeasureForwardGrowth;
