import React, { useState } from "react";
import ImageMarker, { Marker } from "react-image-marker";

function MeasureForwardGrowth() {
  const [image, setImage] = useState(null);
  const [markers, setMarkers] = useState([]);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onloadend = () => {
      setImage(reader.result);
    };

    reader.readAsDataURL(file);
  };

  const labelText = () => {
    if (markers.length <= 2) {
      return "Please Label the 5 cm Marker on this Image";
    } else if (markers.length <= 3) {
      return "Please Label point F";
    }
  };

  return (
    <div>
      <h1>Measure Forward Growth Page</h1>
      <img src="growth-guide.jpeg" />
      <input type="file" accept="image/*" onChange={handleImageUpload} />
      {image && (
        <>
          <h3>Please Label the 5 cm Marker on this Image</h3>
          <ImageMarker
            src={image}
            markers={markers}
            onAddMarker={(marker) => setMarkers([...markers, marker])}
          />
        </>
      )}
    </div>
  );
}

export default MeasureForwardGrowth;
