import React, { useState, useRef } from "react";
import { useLocation } from "react-router-dom";
import ImageMarker from "react-image-marker";

export default function MeasureGrowth() {
  const location = useLocation();
  const { image, imageDimensions } = location.state;
  const [markers, setMarkers] = useState([]);

  const addNewMarker = (marker) => {
    if (markers.length < 3) {
      setMarkers([...markers, marker]);
    }
  };

  return (
    <>
      <div className="flex justify-center">
        <h1 class="text-center text-xl font-semibold">Label 5 CM Marker</h1>
      </div>
      <button className="bg-gray-200 hover:bg-gray-300 border-gray-300 px-32 py-12 text-2xl rounded-xl border-4 focus:border-gray-200 w-1/2">
        ❌ Remove 5 CM Marker
      </button>
      <div className="w-1/3 bg-gray-200 p-4 rounded-xl">
        <ImageMarker
          src={image}
          markers={markers}
          onAddMarker={(marker) => addNewMarker(marker)}
        />
      </div>
      <button>Re-Upload ⬆️</button>
    </>
  );
}
