import React, { useState } from "react";
import referenceImage from "../reference-images/mike-mew.jpg";

export default function UploadFaceProfile() {
  return (
    <>
      <h1>Image Guidelines</h1>
      <h3>Photograph the patient’s face in a true lateral position. </h3>
      <h3>
        Use an ‘Alice’ band to make sure that neither the forehead nor the
        tragus of the ear is obscured with hair.{" "}
      </h3>
      <h3>
        Make sure a scale of centimetres or a 5cm marker is included. This must
        be placed in the midline with the sagittal plane to ensure accuracy.
      </h3>
      <img
        src={referenceImage}
        alt="Sample Gnathiometer Profile Image"
        width="30%"
        height="undefined"
      />
    </>
  );
}
