import React, { useState, useRef, useEffect } from "react";
import { useLocation } from "react-router-dom";
import ImageMarker from "react-image-marker";

export default function MeasureGrowth() {
  const location = useLocation();

  const [image, setImage] = useState(null);
  const [imageDimensions, setImageDimensions] = useState({
    width: 0,
    height: 0,
  });
  const [markers, setMarkers] = useState([]);
  const inputRef = useRef();

  useEffect(() => {
    // Set the image and imageDimensions state variables based on location.state when the component first loads
    setImage(location.state.image);
    setImageDimensions(location.state.imageDimensions);
  }, []);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onloadend = () => {
      const newImage = reader.result;
      setImage(newImage); // Update the state variable

      const img = new Image();
      img.onload = function () {
        const newImageDimensions = { width: this.width, height: this.height };
        setImageDimensions(newImageDimensions); // Update the state variable
      };
      img.src = newImage;
    };

    setMarkers([]);

    reader.readAsDataURL(file);
  };

  const labelText =
    markers.length < 2
      ? "Label 5 CM Marker"
      : "Label Point N (See Growth Guide)";

  const addNewMarker = (marker) => {
    if (markers.length < 3) {
      setMarkers([...markers, marker]);
    }
  };

  const removeLastMarker = () => {
    if (markers.length > 1) {
      setMarkers(markers.slice(0, -1));
    } else if (markers.length == 1) {
      setMarkers([]);
    }
  };

  return (
    <>
      <div className="flex justify-center my-4">
        <h1 class="text-center text-xl font-semibold">{labelText}</h1>
      </div>
      <div className="flex justify-center my-4">
        <button
          className="bg-gray-200 hover:bg-gray-300 border-gray-300 px-32 py-8 text-2xl rounded-xl border-4 focus:border-gray-200 w-1/2"
          onClick={removeLastMarker}
        >
          ❌ Remove Marker
        </button>
      </div>
      <div className="flex justify-center my-4">
        <div className="w-1/3 bg-gray-200 p-4 rounded-xl">
          <ImageMarker
            src={image}
            markers={markers}
            onAddMarker={(marker) => addNewMarker(marker)}
          />
        </div>
      </div>
      <div className="flex justify-center my-4">
        <button
          className="bg-gray-200 hover:bg-gray-300 border-gray-300 px-32 py-8 text-2xl rounded-xl border-4 focus:border-gray-200 w-1/2"
          onClick={() => inputRef.current.click()}
        >
          Re-Upload ⬆️
        </button>
        <input
          type="file"
          accept="image/*"
          onChange={handleImageUpload}
          ref={inputRef}
          style={{ display: "none" }}
        />
      </div>
    </>
  );
}
