import React, { useState, useRef, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";

import ImageMarker from "react-image-marker";
import Draggable from "react-draggable";

import growthGuide from "./growth-guide.jpeg";
import transparentGuide from "./guide.png";

export default function MeasureGrowth() {
  const location = useLocation();

  const { image, imageDimensions } = location.state;
  const [markers, setMarkers] = useState([]);
  const navigate = useNavigate();

  const labelText = () => {
    if (markers.length < 2) {
      return "Label 5 CM Marker";
    } else if (markers.length == 2) {
      return "Label Point N (See Growth Guide)";
    } else {
      return "Overlay Growth Guide (Drag It & Follow Instructions)";
    }
  };

  const fiveCMPixelDistance = () => {
    if (markers.length >= 2) {
      const marker1 = markers[0];
      const marker2 = markers[1];
      const xDist =
        (marker2.left / 100) * imageDimensions.width -
        (marker1.left / 100) * imageDimensions.width;
      const yDist =
        (marker2.top / 100) * imageDimensions.height -
        (marker1.top / 100) * imageDimensions.height;

      return Math.sqrt(Math.pow(xDist, 2) + Math.pow(yDist, 2));
    } else {
      return 0;
    }
  };

  const proportion = fiveCMPixelDistance() / 5 / (window.innerWidth / 3);

  // Assuming the provided dimensions are in cm, convert them to pixel equivalent using the 5cm pixel distance
  const guideWidthInPixels = 17.72 * proportion;
  const guideHeightInPixels = 23.67 * proportion;

  console.log("GUIDO: ", guideWidthInPixels);
  console.log("Five CM Pixel Distance", fiveCMPixelDistance());

  const showGrowthGuide = () => {
    return markers.length === 3;
  };

  const addNewMarker = (marker) => {
    if (markers.length < 3) {
      setMarkers([...markers, marker]);
    }
  };

  const removeLastMarker = () => {
    if (markers.length > 1) {
      setMarkers(markers.slice(0, -1));
    } else if (markers.length == 1) {
      setMarkers([]);
    }
  };

  return (
    <>
      <div className="flex justify-center my-4">
        <h1 class="text-center text-xl font-semibold">{labelText()}</h1>
      </div>
      <div className="flex justify-center my-4">
        <button
          className="bg-gray-200 hover:bg-gray-300 border-gray-300 px-32 py-8 text-2xl rounded-xl border-4 focus:border-gray-200 w-1/2"
          onClick={removeLastMarker}
        >
          ❌ Remove Marker
        </button>
      </div>
      <div className="flex justify-center my-4 space-x-4">
        <div className="w-1/3 bg-gray-200 p-4 rounded-xl">
          <ImageMarker
            src={image}
            markers={markers}
            onAddMarker={(marker) => addNewMarker(marker)}
          />
        </div>
        {markers.length >= 2 && (
          <div className="w-1/3 bg-gray-200 p-4 rounded-xl">
            <img src={growthGuide} alt="Gnathiometer Growth Guide" />
          </div>
        )}
        {showGrowthGuide() && (
          <div
          /*style={{
            transform: `rotate(${degree}deg)`,
          }}*/
          >
            <Draggable /*disabled={guideLocked}*/>
              <img
                className="processedImage"
                src={transparentGuide}
                alt="Processed"
                style={{
                  maxWidth: `${guideWidthInPixels}px`,
                  maxHeight: `${guideHeightInPixels}px`,
                  transformOrigin: "50% 50%",
                  //pointerEvents: guideLocked ? "none" : "auto",
                }}
              />
            </Draggable>
          </div>
        )}
      </div>
      <div className="flex justify-center my-4">
        <button
          className="bg-gray-200 hover:bg-gray-300 border-gray-300 px-32 py-8 text-2xl rounded-xl border-4 focus:border-gray-200 w-1/2"
          onClick={() => navigate("/upload-image-fg")}
        >
          Re-Upload ⬆️
        </button>
      </div>
    </>
  );
}
