import React, { useState, useRef } from "react";
import referenceImage from "../reference-images/mike-mew.jpg";

export default function UploadFaceProfile() {
  const [image, setImage] = useState(null);
  const [imageDimensions, setImageDimensions] = useState({
    width: 0,
    height: 0,
  });
  const inputRef = useRef();

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onloadend = () => {
      setImage(reader.result);

      const img = new Image();
      img.onload = function () {
        setImageDimensions({ width: this.width, height: this.height });
      };
      img.src = reader.result;
    };

    reader.readAsDataURL(file);
  };

  return (
    <div>
      {/* ...other code... */}
      <div class="flex justify-center">
        <button
          class="bg-gray-200 hover:bg-gray-300 border-gray-300 px-32 py-12 text-2xl rounded-xl border-4 focus:border-gray-200 w-1/2"
          onClick={() => inputRef.current.click()}
        >
          Upload Image ⬆️
        </button>
        <input
          type="file"
          accept="image/*"
          onChange={handleImageUpload}
          ref={inputRef}
          style={{ display: "none" }}
        />
      </div>
    </div>
  );
}
