import React, { useState } from "react";
import { Stage, Layer, Line, Circle } from "react-konva";

export function ChinMarker({ position, onDragMove }) {
  // These could be made dynamic if you want
  const lineLength = 50;
  const gapLength = 10;
  const lineColor = "black";
  const circleColor = "red";
  const lineWidth = 2;
  const canvasSize = 500; // set the canvas size

  const [rotation, setRotation] = useState(0);

  // Calculate points for lines
  const leftLinePoints = [0, 0, lineLength / 2 - gapLength / 2, 0];
  const rightLinePoints = [lineLength / 2 + gapLength / 2, 0, lineLength, 0];

  // Circle position
  const circlePosition = { x: lineLength / 2, y: 0 };

  const rotateLine = () => {
    setRotation(rotation + 5);
  };

  return (
    <div>
      <button onClick={rotateLine}>Rotate</button>
      <Stage
        width={canvasSize}
        height={canvasSize}
        draggable
        onDragMove={onDragMove}
        position={position}
      >
        <Layer>
          <Line
            points={leftLinePoints}
            stroke={lineColor}
            strokeWidth={lineWidth}
            rotation={rotation}
            offsetX={5}
          />
          <Line
            points={rightLinePoints}
            stroke={lineColor}
            strokeWidth={lineWidth}
            rotation={rotation}
            offsetX={5}
          />
          <Circle
            radius={lineWidth * 2}
            fill={circleColor}
            {...circlePosition}
          />
        </Layer>
      </Stage>
    </div>
  );
}
