import React, { useState, useEffect } from "react";
import { Stage, Layer, Line, Circle, Group } from "react-konva";

export function ChinMarker({
  position,
  onDragMove,
  imageWidth,
  imageHeight,
  twoMM,
  rotation,
  onCirclePositionChange,
}) {
  const lineLength = 25 * twoMM;
  const gapLength = 5 * twoMM;
  const lineColor = "black";
  const circleColor = "red";
  const lineWidth = twoMM;

  const [circleX, setCircleX] = useState(lineLength / 2);
  const [circleY, setCircleY] = useState(0);

  useEffect(() => {
    // Call the function passed as a prop to send the state up to the parent
    onCirclePositionChange({ x: circleX, y: circleY });
  }, [circleX, circleY]); // Re-run the effect whenever circleX or circleY changes

  return (
    <div>
      <Stage
        width={imageWidth}
        height={imageHeight}
        draggable
        onDragMove={(e) => {
          const newPosition = e.target.position();
          setCircleX(newPosition.x + lineLength / 2);
          setCircleY(newPosition.y);
          onDragMove(e);
        }}
        position={position}
      >
        <Layer>
          <Group rotation={rotation} offsetX={circleX}>
            <Line
              points={[0, 0, lineLength / 2 - gapLength / 2, 0]}
              stroke={lineColor}
              strokeWidth={lineWidth}
            />
            <Line
              points={[lineLength / 2 + gapLength / 2, 0, lineLength, 0]}
              stroke={lineColor}
              strokeWidth={lineWidth}
            />
            <Circle
              radius={lineWidth}
              fill={circleColor}
              x={circleX}
              y={circleY}
            />
          </Group>
        </Layer>
      </Stage>
    </div>
  );
}
