import React, { useState } from "react";
import { Stage, Layer, Line, Circle, Group } from "react-konva";

export function ChinMarker({
  position,
  onDragMove,
  imageWidth,
  imageHeight,
  rotation,
}) {
  const lineLength = 50;
  const gapLength = 10;
  const lineColor = "black";
  const circleColor = "red";
  const lineWidth = 2;

  // Offset coordinates by 20
  const offsetX = 20;
  const offsetY = 20;

  const leftLinePoints = [
    offsetX,
    offsetY,
    offsetX + lineLength / 2 - gapLength / 2,
    offsetY,
  ];
  const rightLinePoints = [
    offsetX + lineLength / 2 + gapLength / 2,
    offsetY,
    offsetX + lineLength,
    offsetY,
  ];

  const circlePosition = { x: offsetX + lineLength / 2, y: offsetY };

  return (
    <div>
      <Stage
        width={imageWidth}
        height={imageHeight}
        draggable
        onDragMove={onDragMove}
        position={position}
      >
        <Layer>
          <Group
            rotation={rotation}
            offsetX={circlePosition.x - offsetX}
            offsetY={circlePosition.y - offsetY}
          >
            <Line
              points={leftLinePoints}
              stroke={lineColor}
              strokeWidth={lineWidth}
            />
            <Line
              points={rightLinePoints}
              stroke={lineColor}
              strokeWidth={lineWidth}
            />
            <Circle
              radius={lineWidth * 2}
              fill={circleColor}
              {...circlePosition}
            />
          </Group>
        </Layer>
      </Stage>
    </div>
  );
}
